import React, { useState } from "react";
import {
  Skeleton,
  Card,
  Typography,
  Image,
  Row,
  Col,
  Tag,
  Button,
  PageHeader,
} from "antd";
import {
  EditOutlined,
  EllipsisOutlined,
  SettingOutlined,
} from "@ant-design/icons";

const { Meta } = Card;
const { Title, Paragraph } = Typography;

const gridStyle = {
  width: "25%",
  textAlign: "center",
  margin: 0,
  padding: 0,
  border: "none",
};
const gridStyle1 = {
  width: "75%",
  border: "none",
};

export const CardNews = () => {
  const [loading, setLoading] = useState(false);

  const onChange = (checked) => {
    setLoading(!checked);
  };

  const Content = ({ children, extraContent }) => {
    return (
      <Row>
        <div style={{ flex: 1 }}>{children}</div>
        <div className="image">{extraContent}</div>
      </Row>
    );
  };
  const IconLink = ({ src, text }) => (
    <a className="example-link">
      <img className="example-link-icon" src={src} alt={text} />
      {text}
    </a>
  );

  const content = (
    <>
      <Paragraph>
        Ant Design interprets the color system into two levels: a system-level
        color system and a product-level color system.
      </Paragraph>
      <Paragraph>
        Ant Design&#x27;s design team preferred to design with the HSB color
        model, which makes it easier for designers to have a clear psychological
        expectation of color when adjusting colors, as well as facilitate
        communication in teams.
      </Paragraph>
      <div>
        <IconLink
          src="https://gw.alipayobjects.com/zos/rmsportal/MjEImQtenlyueSmVEfUD.svg"
          text="Quick Start"
        />
        <IconLink
          src="https://gw.alipayobjects.com/zos/rmsportal/NbuDUAuBlIApFuDvWiND.svg"
          text=" Product Info"
        />
        <IconLink
          src="https://gw.alipayobjects.com/zos/rmsportal/ohOEPSYdDTNnyMbGuyLb.svg"
          text="Product Doc"
        />
      </div>
    </>
  );

  return (
    <Card hoverable bodyStyle={{ margin: 0, padding: 0 }}>
      <Skeleton loading={loading} avatar active>
        {/* <Card.Grid hoverable={false} style={gridStyle}>
          <Image
            width={100}
            src="https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png"
          />
        </Card.Grid> */}
        {/* <Card.Grid hoverable={false} style={gridStyle1}> */}
        <Row>
          <Col flex="120px">
            <Image src="https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png" />
          </Col>
          <Col flex="auto">
            {/* <PageHeader subTitle="24/04/2019 22:54:11">
              <Title level={5}>Card Title</Title>
              <Paragraph>Description This is the description</Paragraph>
            </PageHeader> */}

            <PageHeader
              title="Title"
              className="site-page-header"
              subTitle="This is a subtitle"
              tags={<Tag color="blue">Running</Tag>}
              avatar={{
                src:
                  "https://avatars1.githubusercontent.com/u/8186664?s=460&v=4",
              }}
            >
              <Content
                extraContent={
                  <img
                    src="https://gw.alipayobjects.com/zos/antfincdn/K%24NnlsB%26hz/pageHeader.svg"
                    alt="content"
                    width="100%"
                  />
                }
              >
                {content}
              </Content>
            </PageHeader>

            {/* <Meta
              title="Card title leonardo"
              description="This is the description www.instagram.com"
            /> */}
          </Col>
        </Row>

        {/* </Card.Grid> */}
      </Skeleton>
    </Card>
    // <Card
    //   hoverable
    //   cover={
    //     <img
    //       alt="example"
    //       src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png"
    //     />
    //   }
    // >
    //   <Card.Meta title="Europe Street beat" description="www.instagram.com" />
    // </Card>
    // <Card
    //   hoverable
    //   cover={
    //     <img
    //       alt="example"
    //       src="https://os.alipayobjects.com/rmsportal/QBnOOoLaAfKPirc.png"
    //     />
    //   }
    // >
    //   <Skeleton loading={loading} avatar active>
    //     <Meta
    //       avatar={
    //         <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
    //       }
    //       title="Card title leonardo"
    //       description="This is the description www.instagram.com"
    //     />
    //   </Skeleton>
    // </Card>
  );
};
